import { useState } from 'react';
import { Alert, Button, Pressable, SafeAreaView, StatusBar, StyleSheet, Text, TextInput, View } from 'react-native';
import DropDownPicker from 'react-native-dropdown-picker';

export function SignUpUI({ navigation }) {

  const [getMobile, setMobile] = useState("");
  const [getFName, setFName] = useState("");
  const [getLName, setLName] = useState("");
  const [getPassword, setPassword] = useState("");

  const [open, setOpen] = useState(false);
  const [value, setValue] = useState(null);
  const [items, setItems] = useState([
    { label: 'Employee', value: '1' },
    { label: 'Student', value: '2' }
  ]);

  const ui = (

    <SafeAreaView style={styles.main}>
      <View style={styles.view1}>
        <View style={styles.view2}>
          <Text style={styles.text1}>Mobile :</Text>
          <TextInput style={styles.txtfld} value={getMobile} onChangeText={(text) => setMobile(text)} />
        </View>

        <View style={styles.view2}>
          <Text style={styles.text1}>First Name :</Text>
          <TextInput style={styles.txtfld} value={getFName} onChangeText={(text) => setFName(text)} />
        </View>

        <View style={styles.view2}>
          <Text style={styles.text1}>Last Name :</Text>
          <TextInput style={styles.txtfld} value={getLName} onChangeText={(text) => setLName(text)} />
        </View>

        <View style={styles.view2}>
          <Text style={styles.text1}>Password :</Text>
          <TextInput style={styles.txtfld} value={getPassword} onChangeText={(text) => setPassword(text)} />
        </View>

        <View style={styles.view2}>
          <Text style={styles.text1}>User Type :</Text>
          <DropDownPicker
            open={open}
            value={value}
            items={items}
            setOpen={setOpen}
            setValue={setValue}
            setItems={setItems}
            textStyle={{ fontSize: 20 }}
          />
        </View>

        <View style={styles.view2}>
          <Button title='Sign In' style={styles.btn} onPress={getDetails} />
        </View>
      </View>

    </SafeAreaView>
  );

  function getDetails() {

    const details = {
      mobile: getMobile,
      firstName: getFName,
      lastName: getLName,
      password: getPassword,
      userTypeId: value
    };

    fetch("http://10.0.2.2/Notes_App/register.php", {
      method: "POST",
      body: JSON.stringify(details),
    }).then((response)=>{
        return response.text();
    }).then((text)=>{
       if (text == "Success") {
        Alert.alert("Sign Up Success!!!");
       } else {
        Alert.alert(text);
       }
    }).catch((error)=>{
        Alert.alert("Error",error);
    });
  }

  return ui;
}

const styles = StyleSheet.create({
  main: {
    flex: 1,
  },

  view1: {
    flex: 1,
    backgroundColor: "red",
    backgroundColor: "#dddddd",
    padding: 15,
  },

  view2: {
    marginBottom: 20,
  },

  text1: {
    fontSize: 20,
    color: "black",
    paddingVertical: 10,
  },

  txtfld: {
    borderColor: "black",
    borderWidth: 1,
    borderStyle: "solid",
    borderRadius: 5,
    padding: 5,
  },

  drpdwn: {

  },

  btn: {
    backgroundColor: "red",
    height: 20,

  }
});
